import React from "react";
import Avatar from "../components/UI/Avatar";
import { useLoaderData } from "react-router-dom";


const ArticlePage = () => {
    const article = useLoaderData();
    return (
        <div className="w-full flex flex-col px-8">
            <div className="w-full border-b border-black py-4 my-4">
                <h1 className="text-4xl font-semibold">{article.title}</h1>
                <div className="h-12 flex items-center">
                    <Avatar image = "/images/courses.jpg"/>
                    <h5 className="p-4 text-md">{article.author}</h5>
                </div>
                <p className="text-md text-gray-600">14 Nov</p>
            </div>
            <div>
                <p>C++ is the most used and most popular programming language developed by Bjarne Stroustrup. 
                    C++ is a high-level and object-oriented programming language.This language allows developers to write clean and efficient code for large applications and software development,
                    game development, and operating system programming. 
                    It is an expansion of the C programming language to include Object Oriented Programming(OOPs) and is used to develop programs for computers. 
                    This C++ Tutorial will cover all the basic to advanced topics of C++ like C++ basics, C++ functions, C++ classes, OOPs and STL concepts
                </p>
            </div>
        </div>
    )
}


export async function loader({request,params}){
    const articleId = params.articleId;
    //Fetch the data from backend with article id for now using dummy response
    const response = {
        id:"article-2",
        title:"C++ Programming Language",
        content:"C++ is the most used and most popular programming language developed by Bjarne Stroustrup.",
        thumbnailImg:"./images/ai.jpg",
        userAvatar:"./images/courses.jpg",
        author:"Faiz Khan",
        date:"19 Nov",
        tags:[
            "Operating Systems",
        ]
    };

    return response;
}



export default ArticlePage;
