import React from "react";
import Image from "../components/Icons/Image";

const ArticleUpload = () => {
    return (
        <div className="w-4/5 mx-auto my-12">
            <div className="w-full h-16 bg-gray-800 flex justify-center items-center">
                <h1 className="text-white text-xl">Publish Your Article</h1>
            </div>
            <form className="w-full h-full flex flex-col">
                <button className="w-48 p-2 flex justify-around items-center bg-gray-800 rounded-full self-end text-white my-4" type = "button"><Image/> Upload Cover</button>
                <input className="w-full text-4xl font-semibold p-4 outline-none border-b border-gray-400 my-4" type = "text" name = "title" placeholder = "Title"/>
                <textarea className = "w-full p-4 outline-none my-4" name = "content" placeholder="Write here.." rows={10}/>
                <input className="w-full p-4 outline-none my-4" type = "text" name = "tag" placeholder="Enter Relevant Tag then press Enter"/>
                <button className = "" type = "submit">Publish</button>
            </form>
        </div>
    );
}

export default ArticleUpload;
